CREATE DATABASE ecommerce2 DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci;

USE ecommerce2;

CREATE TABLE utilisateur (
  code        VARCHAR(255),
  nom         VARCHAR(255),
  prenom      VARCHAR(255),
  email       VARCHAR(255),
  mdp         VARCHAR(255),
  telephone   VARCHAR(255),
  CONSTRAINT user_pk PRIMARY KEY (code)
);


CREATE TABLE livre (
  numero           VARCHAR(255),
  titre            VARCHAR(255),
  auteur           VARCHAR(255),
  description      TEXT,
  date_publication DATE,
  nbr_pages        INT,
  editeur          VARCHAR(255),
  edition          VARCHAR(255),
  isbn10           VARCHAR(255),
  isbn13           VARCHAR(255),
  prix             DECIMAL(10,2),
  categorie        VARCHAR(255),
  label            VARCHAR(255),
  chemin_img       VARCHAR(255),
  quantite         INT,
  CONSTRAINT numero_pk PRIMARY KEY (numero)
);

-- Policier


-- Science-fiction

-- Poésie

-- Histoire
  
CREATE TABLE commande (
  numero_commande  VARCHAR(255),
  code             VARCHAR(255),
  date_commande    DATE,
  total            DECIMAL(10,2),
  CONSTRAINT numero_commande_pk PRIMARY KEY (numero_commande),
  CONSTRAINT code_fk FOREIGN KEY (code) REFERENCES utilisateur(code)
);

CREATE TABLE commande_livres (
  numero_commande  VARCHAR(255),
  numero           VARCHAR(255),
  CONSTRAINT commande_livres_pk PRIMARY KEY (numero_commande, numero),
  FOREIGN KEY (numero_commande) REFERENCES commande(numero_commande),
  FOREIGN KEY (numero) REFERENCES livre(numero)
);
